import { Component, Vue, Prop, Emit, Model, Watch } from 'vue-property-decorator'

@Component
export default class Radio extends Vue {
  @Prop() options!: any[]
  @Model('update', {
    type: Number
  })
  val!: number

  get checked2 () {
    return this.val
  }

  //   set checked2 (val) {
  //     //   this.checked2 = val
  //   }

  @Emit('update')
  private clickRadio (val: number) {
    // this.checked2 = val
    return val
  }

  @Watch('checked2')
  onChecked2 (val: number, oldVal: number) {
    console.log('我是子组件，现在的值为：' + val)
  }
}
